public class AvgMarks {
	
	public static int Marks( int a,int b, int c) {
		return (a+b+c)/3;
		
	}

	public static void main(String[] dhanashri) {
		// TODO Auto-generated method stub
		
		System.out.println("Avrage of:"+ Marks(60,65,72));
		System.out.println();

	}

}
